                    <Crwnlogo  className="logo"/>
                </Link>
                <div className="nav-links-container">
                    <Link className="nav-link" to='/shop'>
                        SHOP
                    </Link>
                    {currentUser}:{
                           <Link className="nav-link" to='/signin' onClick={SignOut}>
                           SIGN OUT 
                         </Link>
                    }?{
                        <Link className="nav-link" to='/signin'>
