import React, { useState } from 'react'
import { createAuthUserWithEmailAndPassword } from '../../utils/firebase/firebase.utils';
const defaultformstate={username:'',
                          email:'',
                          password:'',
                          confpass:''};

const SignUpForm = () =>
{
  const [formfields,setformFields] = useState({defaultformstate})
  const {username,email,password,confpass} = formfields;
  console.log(formfields)

  const handleChange=(event)=>{
    const {name,value} = event.target;
    setformFields({...formfields,[name]:value});
  }
  return (
    <div className='signup form'>
      <form onSubmit={()=>{}}>
        <label>User Name</label>
        <input type='text' required onChange={handleChange}  name='Username' value={username}></input>
        <label>Email</label>
        <input type='email' required onChange={handleChange} name='email' value={email}></input>
        <label>Password</label>
        <input type='password' required onChange={handleChange} name='password' value={password} ></input>
        <label>Confirm Password</label>
        <input type='password' required onChange={handleChange} name='confpass' value={confpass}></input>
        <button type='submit'></button>
      </form>
    </div>
  )
}
export default SignUpForm
