import React, { useState } from 'react'
import { createAuthUserWithEmailAndPassword , createUserDocumentFromAuth } from '../../utils/firebase/firebase.utils';
import FormInput from '../form-input/form-input.component';
import '../sign-up-form/sign-up-form.styles.scss';

const defaultformstate={displayName:'',
                          email:'',
                          password:'',
                          confpass:''
                        };


const SignUpForm = () =>
{
  const [formfields,setformFields] = useState({defaultformstate})
  const {displayName,email,password,confpass} = formfields;

  const resetFormFields = () => {
    setformFields(defaultformstate);
  };

  const handleSubmit=async(event)=>{
    event.preventDefault();

    if (password!==confpass){
      alert("password do not match")
      return
    }

    try{
      const {user}  = await createAuthUserWithEmailAndPassword(
        email,
        password
      );
      await createUserDocumentFromAuth( user,{displayName});
      resetFormFields();
    }
    catch (error) {
      if (error.code === 'auth/email-already-in-use') {
        alert('Cannot create user, email already in use');
      } else {
        console.log('user creation encountered an error', error);
      }
    }
  };

  const handleChange=(event)=>{
    const {name,value} = event.target;
    setformFields({...formfields,[name]:value});
  }
  return (
    <div className='sign-up-container'>
      <h2>Don't have an account?</h2>
      <span>Sign up with your email and password</span>
      <form onSubmit={handleSubmit}>
        <FormInput label="User Name" type='text' required onChange={handleChange}  name='displayName' value={displayName}></FormInput>
        <FormInput label="Email"type='email' required onChange={handleChange} name='email' value={email}></FormInput>
        <FormInput label="Password" type='password' required onChange={handleChange} name='password' value={password} ></FormInput>
        <FormInput label="Confirm Password" type='password' required onChange={handleChange} name='confpass' value={confpass}></FormInput>
        <button type='submit'>submit</button>
      </form>
    </div>
  )
}
export default SignUpForm
