import React, { useState } from 'react'
import { createAuthUserWithEmailAndPassword , createUserDocumentFromAuth } from '../../utils/firebase/firebase.utils';
const defaultformstate={displayName:'',
                          email:'',
                          password:'',
                          confpass:''};


const SignUpForm = () =>
{
  const [formfields,setformFields] = useState({defaultformstate})
  const {displayName,email,password,confpass} = formfields;

  const resetFormFields = () => {
    setFormFields(defaultFormFields);
  };

  const handleSubmit=async(event)=>{
    event.preventDefault();

    if (password!==confpass){
      alert("password do not match")
      return
    }

    try{
      const {user}  = await createAuthUserWithEmailAndPassword(
        email,
        password
      );
      await createUserDocumentFromAuth( user,{displayName});
      resetFormFields();
    }
    catch(error){
      console.log("user creation ecountered an error",error);
    }
  };


  const handleChange=(event)=>{
    const {name,value} = event.target;
    setformFields({...formfields,[name]:value});
  }
  return (
    <div className='signup form'>
      <form onSubmit={handleSubmit}>
        <label>User Name</label>
        <input type='text' required onChange={handleChange}  name='displayName' value={displayName}></input>
        <label>Email</label>
        <input type='email' required onChange={handleChange} name='email' value={email}></input>
        <label>Password</label>
        <input type='password' required onChange={handleChange} name='password' value={password} ></input>
        <label>Confirm Password</label>
        <input type='password' required onChange={handleChange} name='confpass' value={confpass}></input>
        <button type='submit'>submit</button>
      </form>
    </div>
  )
}
export default SignUpForm
